(ns fetcher.callback-test
  (:require [fetcher.core :as fetch]
            [work.queue :as workq]
            [work.core :as work]
            (clj-time [core :as time]
                      [coerce :as time-coerce])
            [swank.core :as swank])
  (:use clojure.test
	work.graph
	fetcher.callback
        [plumbing.core :only [with-silent with-wait with-log
                              with-timeout wait-until]])
  (:import [java.util.concurrent ConcurrentHashMap]))

(declare ok-resp
         temp-redirect-resp
         perm-redirect-resp
         perm-redirect-to-same-resp
         feeds)

(def poll-req-q (workq/local-queue))
(def poll-resp-q (workq/local-queue))

(def store (ConcurrentHashMap.))
(def get-feed #(.get store %))
(def set-feed #(.put store %1 %2))
(def rm-feed #(.remove store %))

(defn past-5-min?
  [t]
  (time/within? (time/interval (time/minus (time/now) (time/minutes 5))
                               (time/now))
                t))

(defn reset
  [f]
  (.clear poll-req-q)
  (.clear poll-resp-q)
  (.clear store)
  (doseq [[k f] feeds]
    (.put store k f))
  (f))

(use-fixtures :each reset)

;; TODO Need tests for each implementation configuration
(deftest ok-handler
  (let [update-fetch (mk-update-fetch get-feed set-feed)
        update-feed (mk-update-feed get-feed set-feed)
        put-ok #(workq/offer poll-resp-q %)
        handler (:200 (fetch/response-table update-fetch update-feed put-ok))
        feed-key "http://daringfireball.net/index.xml"
        resp-headers (:headers ok-resp)]
    (handler ok-resp)
    (let [feed (get-feed feed-key)]
      (is (= (:last-modified resp-headers)
             (:last-modified feed)))
      (is (= (:etag resp-headers)
             (:etag feed)))
      (is (past-5-min? (time-coerce/from-string (:last-fetched feed)))))))

(deftest perm-redirect-handler
  (let [move-feed (mk-move-feed get-feed set-feed rm-feed)
        update-feed (mk-update-feed get-feed set-feed)
        update-fetch (mk-update-fetch get-feed set-feed)
        put-redirect #(workq/offer poll-req-q %)
        handler (:301 (fetch/redirect update-feed put-redirect update-fetch move-feed))
        feed-key "http://feedproxy.google.com/TechCrunch"
        resp-headers (:headers perm-redirect-resp)]
    (handler perm-redirect-resp)
    (let [feed (get-feed feed-key)]
      (is (= "http://feeds.feedburner.com/TechCrunch" (:url feed)))
      ;; Need to see why timestamp string isn't be parsed right
      ;;(is (past-5-min? (time-coerce/from-string (:last-modified feed))))
      ;;(is (past-5-min? (time-coerce/from-string (:etag feed))))
      (is (= (:last-modified feed)
             (:etag feed)))
      (is (past-5-min? (time-coerce/from-string (:last-fetched feed)))))))

(deftest temp-redirect-handler
  (let [update-fetch (mk-update-fetch get-feed set-feed)
        put-redirect #(workq/offer poll-req-q %)
        handler (:300 (fetch/redirect update-fetch put-redirect))
        feed-key "http://feedproxy.google.com/TechCrunch"
        new-key "http://feeds.feedburner.com/TechCrunch"
        resp-headers (:headers temp-redirect-resp)]
    (handler temp-redirect-resp)
    (let [feed (get-feed feed-key)]
      (is (= feed-key (:url feed)))
      (is (= (:last-modified resp-headers)
             (:last-modified feed)))
      (is (= (:etag resp-headers)
             (:etag feed)))
      (is (time/within? (time/interval
			 (time/minus (time/now)
				     (time/minutes 5))
			 (time/now))
                        (time-coerce/from-string (:last-fetched feed)))))))

(deftest same-old-and-new-perm-redirect
  (let [feed-key "http://reflective"
        feed-url "http://reflective"
        move-feed (mk-move-feed get-feed set-feed rm-feed)
        update-feed (mk-update-feed get-feed set-feed)
        update-fetch (mk-update-fetch get-feed set-feed)
        put-redirect #(workq/offer poll-req-q %)
        handler (:301 (fetch/redirect
		       update-feed
		       put-redirect
		       update-fetch
		       move-feed))]
	(set-feed feed-key {:url feed-url})
	(handler perm-redirect-to-same-resp)
	(is (not (nil? (get-feed feed-key))))
	(is (= feed-url (-> (get-feed feed-key) :url)))
	(is (= 1 (.size poll-req-q)))))

(defn mk-test-feed-fetcher
  []
  (work/queue-work
   {:f fetch/fetch
    :in #(workq/poll poll-req-q)
    :out (dispatch fetch/on-response
	     (fetch/response-table
	      (mk-update-fetch get-feed set-feed)
	      (mk-update-feed get-feed set-feed)
	      #(workq/offer poll-resp-q %))
	     (fetch/redirect
	      (mk-update-feed get-feed set-feed)
	      #(workq/offer poll-req-q %)
	      (mk-update-fetch get-feed set-feed)
	      (mk-move-feed get-feed set-feed rm-feed)))
    :exec work/async}))

(deftest feed-fetch
  (let [fetch-pool (mk-test-feed-fetcher)
        feed-key "http://daringfireball.net/index.xml"
        feed (feeds feed-key)]
    (workq/offer poll-req-q {:key feed-key
                             :url feed-key})
    (wait-until #(= 1 (count poll-resp-q)) 30)
    (let [{k :key b :body} (workq/poll poll-resp-q)]
      (is (= 0 (count poll-resp-q)))
      (is (= feed-key k))
      (is (not (nil? b))))
    (.shutdown fetch-pool)))

